= HERO_FIRE_EVENT:<br>                # print(\"发射子弹\")<br>                self.hero.fire()<br><br>            elif event.type == BGM:<br>                pygame.mixer_music.load(\"images/bgmusic.mp3\")<br>                pygame.mixer_music.play()<br>                pygame.mixer.music.set_endevent(BGM + 1)<br><br>            elif event.type == BGM + 1:<br>                pygame.mixer_music.load(\"images/bgmusic.mp3\")<br>                pygame.mixer_music.play(-1)<br><br>            # elif event.type == pygame.MOUSEMOTION:<br>            #     # return the X and Y position of the mouse cursor<br>            #     pos = pygame.mouse.get_pos()<br>            #     mouse_x = pos[0]<br>            #     mouse_y = pos[1]<br>            #     self.hero.rect.centerx = mouse_x<br>            #     self.hero.rect.y = mouse_y - self.hero.rect.height / 2<br><br>        # elif event.type == pygame.KEYDOWN and event.key == pygame.K_RIGHT:<br>        #    print(\"向右移动\")<br><br>        # 使用键盘模块提供的方法获取键盘按钮 元组<br>        keys_pressed = pygame.key.get_pressed()<br>        # 判断元祖中对应的按键索引值<br>        if keys_pressed[pygame.K_RIGHT] or keys_pressed[pygame.K_d]:<br>            # print(\"向右移动\")<br>            self.hero.rect.x += 3<br><br>        elif keys_pressed[pygame.K_LEFT] or keys_pressed[pygame.K_a]:<br>            # print(\"向左移动\")<br>            self.hero.rect.x -= 3<br><br>        elif keys_pressed[pygame.K_UP] or keys_pressed[pygame.K_w]:<br>            # print(\"向上移动\")<br>            self.hero.rect.y -= 3<br><br>        elif keys_pressed[pygame.K_DOWN] or keys_pressed[pygame.K_s]:<br>            # print(\"向下移动\")<br>            self.hero.rect.y += 3<br><br>        elif keys_pressed[pygame.K_ESCAPE]:<br>            PlaneGame.__game_over()<br><br>    # 碰撞检测<br>    def _check_collide(self):<br>        pygame.sprite.groupcollide(self.hero.bullets, self.enemy_group, True, True)<br><br>        enemies = pygame.sprite.spritecollide(self.hero, self.enemy_group, True)  # 返回值为敌机的列表<br><br>        # 判断列表是否有内容<br>        if len(enemies) &gt; 0:<br>            # 让英雄牺牲<br>            self.hero.kill()<br><br>            # 结束游戏<br>            PlaneGame.__game_over()<br><br>    # 更新精灵组<br>    def __update_sprites(self):<br>        self.back_group.update()<br>        self.back_group.draw(self.screen)<br><br>        self.enemy_group.update()<br>        self.enemy_group.draw(self.screen)<br><br>        self.hero_group.update()<br>        self.hero_group.draw(self.screen)<br><br>        self.hero.bullets.update()<br>        self.hero.bullets.draw(self.screen)<br><br>    # 游戏结束<br>    @staticmethod  # 静态方法<br>    def __game_over():<br>        print(\"游戏结束\")<br><br>        pygame.quit()  # 卸载模块，释放内存<br>        exit()  # 终止游戏<br><br><br>if __name__ == \'__main__\':<br><br>    # 创建游戏对象<br>    game = PlaneGame()<br><br>    # 启动游戏<br>    game.start_game()<br><br>    while True:<br>        pass<br><br></pre><p><br></p>',1,1,255,1,'2021-06-26 21:40:46.863614','2021-06-26 21:40:46.000000',0,0,178,0,1,40,0),(390,'Python实现飞机大战小游戏(工具包文件)','image/default_blog_avatar.jpg','import pygameimport random# 定义屏幕常量# SCREEN_RECT = pygame.Rect(0, 0, 1200, 1080)SCREEN_RECT = pygame.Rect(0, 0, 1200, 800)# 定义刷新的帧率的常量FRAME_PER_SEC = 60# 创建敌机的定时常量CREAT_ENEMY_EVENT = pygame.USEREVENT# ','<pre>import pygame<br>import random<br><br># 定义屏幕常量<br># SCREEN_RECT = pygame.Rect(0, 0, 1200, 1080)<br>SCREEN_RECT = pygame.Rect(0, 0, 1200, 800)<br># 定义刷新的帧率的常量<br>FRAME_PER_SEC = 60<br># 创建敌机的定时常量<br>CREAT_ENEMY_EVENT = pygame.USEREVENT<br><br># 英雄发射子弹事件<br>HERO_FIRE_EVENT = pygame.USEREVENT + 1<br><br># 背景音乐常量<br>BGM = pygame.USEREVENT + 2<br><br><br>class GameSprite(pygame.sprite.Sprite):<br>    \"\"\"飞机大战的游戏精灵\"\"\"<br><br>    def __init__(self, image_name, speed=1):<br>        # 当某一个子类的父类不是object基类时，一定要在初始化<br>        # 方法中，主动调用父类的初始化方法<br>        # 调用父类初始化方法<br>        super().__init__()<br><br>        # 定义对象的属性<br>        # 图像 从图像文件中加载数据，并传给image<br>        self.image = pygame.image.load(image_name)<br>        # 位置 调用get_rect（）方法，返回位置信息，并用变量rect记录<br>        self.rect = self.image.get_rect()<br>        # 速度 默认速度为1<br>        self.speed = speed<br><br>    def update(self):<br>        # 在屏幕的垂直方向移动<br>        self.rect.y += self.speed<br><br><br>class Background(GameSprite):  # 继承自精灵类<br>    \"\"\"游戏背景精灵\"\"\"<br><br>    def __init__(self, is_alt=False):<br><br>        # 1.调用父类方法实现精灵创建<br>        super().__init__(\"./images/background2.jpg\")<br><br>        # 2判断是否为交替图像，如果是，需要设置初始位置<br>        if is_alt:<br>            self.rect.y = -self.rect.height<br><br>    # 重写父类的方法<br>    def update(self):<br><br>        # 1.调用父类的方法实现（垂直方向的移动）<br>        super().update()<br><br>        # 2.判断是否移出屏幕，如果移出，将图像设置在屏幕的正上方<br>        if self.rect.y &gt;= SCREEN_RECT.height:<br>            self.rect.y = -self.rect.height<br><br><br># 定义Enemy类<br>class Enemy(GameSprite):<br>    \"\"\"敌机精灵\"\"\"<br><br>    def __init__(self):<br><br>        # self.image = pygame.image.load(path)<br><br>        # 1.调用父类方法，创建敌机精灵，同时指定敌机图片<br><br>        a = random.randint(0, 2)<br><br>        if a == 0:<br>            super().__init__(\"./images/enemy1.png\")<br><br>        elif a == 1:<br>            super().__init__(\"./images/enemy2.png\")<br><br>        else:<br>            super().__init__(\"./images/enemy3.png\")<br><br>        # super().__init__(\"enemies_list[0]\")<br><br>        # super().__init__(\"./images/enemy2.png\")<br><br>        # 2.指定敌机的初始速度 1~3<br>        self.speed = random.randint(1, 4)<br><br>        # 3.指定敌机的初始位置<br>        self.rect.bottom = 0<br>        self.rect.x = random.randint(0, SCREEN_RECT.width - self.rect.width)<br><br>    def update(self):<br><br>        # 1.调用父类的方法，保持垂直方向的飞行<br>        super().update()<br><br>        # 2判断是否飞出屏幕，如果飞出屏幕，kill该精灵<br>        if self.rect.y &gt; SCREEN_RECT.height:<br>            # print(\"删除该敌机精灵\")<br>            # 删除该敌机，释放内存<br>            self.kill()<br><br>    def __del__(self):<br>        # print(\"敌机挂了%s\"%self.rect)<br>        pass<br><br><br>class Hero(GameSprite):<br>    \"\"\"英雄精灵\"\"\"<br><br>    def __init__(self):<br><br>        # 1.调用父类方法，设置image，speed<br>        super().__init__(\"./images/hero.png\", 0)<br><br>        # 2.英雄的初始位置<br>        self.rect.centerx = SCREEN_RECT.centerx<br>        self.rect.bottom = SCREEN_RECT.bottom - 120<br><br>        # 创建子弹的精灵组<br>        self.bullets = pygame.sprite.Group()<br><br>    def update(self):<br>        \"\"\"控制英雄不能离开屏幕\"\"\"<br><br>        if self.rect.x &lt; 0:<br>            self.rect.x = 0<br><br>        elif self.rect.right &gt; SCREEN_RECT.right:<br>            self.rect.right = SCREEN_RECT.right<br><br>        elif self.rect.y &lt;= 0:<br>            self.rect.y = 0<br><br>        elif self.rect.y &gt;= SCREEN_RECT.height - self.rect.height:<br>            self.rect.y = SCREEN_RECT.height - self.rect.height<br><br>    def fire(self):<br>        # print(\"发射子弹\")<br><br>        for i in (0, 1, 2):<br>            # 一次性发射3颗子弹<br>            # 1.创建子弹精灵<br>            bullet = Bullet()<br><br>            # 设置子弹的位置<br>            bullet.rect.bottom = self.rect.y - i * 20<br>            bullet.rect.centerx = self.rect.centerx<br><br>            # 把子弹精灵添加到子弹精灵组<br>            self.bullets.add(bullet)<br><br><br>class Bullet(GameSprite):<br>    \"\"\"子弹精灵\"\"\"<br><br>    def __init__(self):<br><br>        # 调用父类方法，设置子弹的图片，速度<br><br>        super().__init__(\"./images/bullet1.png\", -6)<br>        keys_pressed = pygame.key.get_pressed()<br>        if keys_pressed[pygame.K_l]:  # 大招:<br>            super().__init__(\"./images/bullet2-4.png\", -6)<br><br>    def update(self):<br><br>        # 调用父类方法，让子弹沿着垂直方向飞行<br>        super().update()<br><br>        # 判断子弹是否飞出屏幕<br>        # 删除子弹<br>        if self.rect.bottom &lt; 0:<br>            self.kill()<br><br>    def __del__(self):<br>        pass<br>        # print(\"子弹被销毁\")<br></pre><p><br></p>',1,1,569,1,'2021-06-26 21:42:23.821740','2021-06-26 21:42:23.000000',0,0,136,0,1,40,0),(391,'学生成